import React, { Component } from 'react';

class Post extends Component {
    state = {  }
    render() { 
        return (
            <div style={styles.postContainer}>
                <div style={styles.sideBar}>
                </div>
                <div style={styles.main}>
                </div>
            </div>
        );
    }
}

const styles = {
    postContainer: {
        display: 'flex',
        flexDirection: 'row',
        backgroundColor: 'white',
        width: '40%',
        height: '300px',
        borderRadius: 10,
        boxShadow: '-1px 0 5px 0 rgba(0, 0, 0, .1)'
    },
    main:{
        width: '90%',
        height: '100%',
        backgroundColor: 'red',
        borderRadius: 10
    },
    sideBar:{
        backgroundColor: 'blue',
        width: '10%',
        height: '100%'
    },
    sideBarText:{
        color: 'gray',
        fontFamily: 'Avenir',
        fontSize: '12pt',
        textAlign: 'center'
    }
}

export default Post;
