                    {/* Profile */}
                    <div>
                        <p>Username</p>
                    </div>
                </div>
            </div>
        );
    }
}

const styles = {
    postContainer: {
        display: 'flex',
        flexDirection: 'row',
        backgroundColor: 'white',
        width: '40%',
        borderRadius: 10,
        boxShadow: '-1px 0 5px 0 rgba(0, 0, 0, .1)'
    },
    main:{
        width: '90%',
        height: '100%',
        marginLeft: 20,
        backgroundColor: 'white',
        borderRadius: '0px 10px 10px 0px'
    },
    sideBar:{
        backgroundColor: 'white',
        width: '10%',
        height: '100%',
        borderRadius: '10px 0px 0px 10px'
    },
    sideBarText:{
        color: 'gray',
        fontFamily: 'Avenir',
        fontSize: '12pt',
        textAlign: 'center'
    },
    titleStyle: {
        fontFamily: 'Merriweather',
        marginTop: 20,
        fontSize: '16pt'
    },
    bodyTextStyle: {
        width: '90%'
    },
    readMore:{
        color: 'blue'
    }
}

export default Post;
